emailTemplates.template:
{{#if deleteApprovedRequired}}
  {{{i18n "gomorpheus.email.instanceExpired.deleteApprovalRequired" user.displayName instance.displayName instance.site.name}}}
{{else if delayedRemovalApplied}}
  {{{i18n "gomorpheus.email.instanceExpired.delayedRemovalApplied" user.displayName instance.displayName instance.site.name}}}
{{else}}
  {{{i18n "gomorpheus.email.instanceExpired" user.displayName instance.displayName instance.site.name}}}
{{/if}}

{{#if success}}
  <p>{{{i18n "gomorpheus.email.instanceComplete" user.displayName instance.displayName}}}</p>
{{else}}
  <p>{{{i18n "gomorpheus.email.instanceCompleteError" user.displayName instance.displayName}}}</p>
{{/if}}
{{#if instance.statusMessage}}
  <p>{{instance.statusMessage}}</p>
{{/if}}
{{#if instance.errorMessage}}
  <p>{{instance.errorMessage}}</p>
{{else}}
  <a href='{{instanceUrl}}' style="display: inline-block;    margin-top:38px;    color: #fff;    background-color:#b33535;    height:48px;    line-height: 48px;    padding: 0 36px;    text-align: center;    text-decoration: none;    font-size: 16px;    border: 1px solid #53c68e;    border-radius: 2px;    font-size: 18px;">{{{i18n "gomorpheus.action.viewInstance"}}}</a>
{{/if}}

{{#if success}}
  <p>{{{i18n "gomorpheus.email.instanceScaleComplete" user.displayName instance.displayName}}}</p>
{{else}}
<p>{{{i18n "gomorpheus.email.instanceScaleCompleteError" user.displayName instance.displayName}}}</p>
{{/if}}
<p>
  <a href='{{instanceUrl}}' style="display: inline-block;    margin-top:38px;    color: #fff;    background-color:#b33535;    height:48px;    line-height: 48px;    padding: 0 36px;    text-align: center;    text-decoration: none;    font-size: 16px;    border: 1px solid #53c68e;    border-radius: 2px;    font-size: 18px;">{{{i18n "gomorpheus.action.viewInstance"}}}</a>
</p>
